module.exports = function maker (types, filters, assign) {
    return function make (el, p) {
        var factory = types[p.type];
        if (!factory) return;

        var port = factory(el, p.param);

        p.filters.forEach(function (f) {
            f = filters[f];
            if (f)
                port = f(port);
        });

        assign(p.path, port);
    }
}
