var P = require('../port');

module.exports = function attrPort (el, name) {
    var $el = $(el);

    return P.create(
        function setter (val) {
            val != null ? $el.attr(name, val) : $el.removeAttr();
        },
        function getter (val) {
            return $el.attr(name);
        }
    );
}
