var P = require('../port');

module.exports = function attrTogglePort (el, name) {
    var $el = $(el);

    return P.create(
        function setter (val) {
            val ? $el.attr(name, true) : $el.removeAttr();
        },
        function getter () {
            return $el.attr(name) ? true : false;
        }
    );
}
