var P = require('..');

module.exports = function attrTogglePort (el, name) {
    var $el = $(el);

    return P.port(
        function setter (val) {
            val ? $el.attr(name, true) : $el.removeAttr(name);
        },
        function getter () {
            return $el.attr(name) ? true : false;
        }
    );
}
