var P = require('..');

module.exports = function cssValuePort (el, initialValue) {
    var $el = $(el);
    var prev = initialValue || '';
    return P.port(
        function setter (val) {
            $el.removeClass(prev);
            $el.addClass(val);
            prev = val; 
        },
        function getter () {
            return prev;
        }
    );
}
