var P = require('../port');

module.exports = function cssValuePort (el, initialValue) {
    var $el = $(el);
    var prev = initialValue || '';

    function port (val) {
        if (arguments.length == 0)
           return prev;
        $el.removeClass(prev);
        $el.addClass(val);
        prev = val; 
    }

    return port;
}
