var P = require('..');

module.exports = function createEventPort (el, ev) {
    var e, port;

    $(el).on(ev, function (_e) {
        e = _e;
        port.onchange();
        e = undefined;
    });

    port = P.listenable(
        function setter () {},
        function getter () {
            return e;
        }
    );

    return port;
}
