var P = require('./port');

module.exports = function propertyPort (initialValue) {
    var events = Object.create(Backbone.Events);
    var _val = initialValue;
    
    return P.createListenable(
        function setter (val) {
            if (_val === val) return;
            _val = val;
            events.trigger('change');
        },
        function getter() {
            return _val;
        },
        events
    );
}
