var P = require('..');
var register = require('./dependency-detection').register;

module.exports = function propertyPort (initialValue) {
    var _val = initialValue;

    var setHook = passThrough;
    var getHook = passThrough;
    
    var p = P.listenable(
        function setter (val) {
            try {
                val = setHook(val);
            }
            catch (e) {
                return;
            }
            if (_val === val) return;
            _val = val;
            p.onchange();
        },
        function getter () {
            register(p);
            return getHook(_val);
        }
    );

    p.hookSetter = function (hook) {
        setHook = Hook(setHook, hook);
        return p;
    }

    p.hookGetter = function (hook) {
        getHook = Hook(getHook, hook);
        return p;
    }

    p.mix = function (fn) {
        fn(p);
        return p;
    }

    return p;
}

function Hook (fn, hook) {
    return function (val) {
        return fn(hook(val));   
    }
}

function passThrough (val) {
    return val;
}
