var P = require('ports');
var Model = require('./view-model');

module.exports = function init (p) {
    window.F = new Model();
    
    $('*', p.el).ports(window.F, null, filters);

    p._show();   
}

var filters = {
    range: P.conversionExit(
        null,
        function output (val) {
            return require('range').toString(val);
        }
    )
}
