module.exports = {
    reset: function () {
        for (var key in this) {
            var prop = this[key];
            var type = typeof prop;
            var isObject = type == 'function' || type == 'object';
            if (prop != null && isObject && prop.reset)
                prop.reset();
        }
    },
    hasError: function () {
        for (var key in this) {
            var prop = this[key];
            var type = typeof prop;
            var isObject = type == 'function' || type == 'object';
            if (prop != null && isObject && prop.hasError && prop.hasError())
                return true;
        }
        return false;
    }
}
