var P = require('ports');

module.exports = Form;

function Form () {}

Form.prototype = Object.create(require('./nodes'));

Form.prototype.reset = function () {
    this.call('reset');
}

Form.hasError = function () {
    var hasError = false;
    this.call('hasError', function (_hasError) {
        hasError = _hasError;
        if (hasError) 
            return 'end';
    });
    return hasError;
}

Form.skip = function (f) {
    f.toJSON = function () {}
}

Form.Field = require('./field');
