module.exports = function init (p) {
    var state = {};

    p.onsuspend = function () {
        state = {
            pageXOffset: window.pageXOffset,
            pageYOffset: window.pageYOffset
        }
        return true;
    }

    p.onresume = function () {
        this._show();
        window.scroll(state.pageXOffset || 0, state.pageYOffset || 0);
    }
}
