var should = require('should');
var R = require('../lib/range');
var I = R.Interval;

describe('Range', function () {
    it('test Interval()', function () {
        I(10, 20).should.eql({low: 10, high: 20});
        I(10, 10).should.equal(10);

        try {
            I(20, 10);
            should.fail();
        }
        catch (e) {}
    });

    it('test toString()', function () {
        function test (range, string) {
            R.toString(range).should.equal(string);
        }

        test(10, '10');
        test({ lowest: 100 }, '> 100');
        test({ highest: 200 }, '< 200');
        test(I(10, 20), '10 - 20');
        test([10, 20], '10; 20');
    });   

    it('test parse()', function () {
        R.parse(' 10 ').should.equal(10);
        R.parse('10; 20').should.eql([10, 20]);
        R.parse('10 - 20').should.eql(I(10, 20));
        R.parse(' > 10').should.eql({ lowest: 10 });
        R.parse(' < 10').should.eql({ highest: 10 });
    });
});
